            { "ITM_BDG_Info_0324", "A pin inlaid with two orange jewels. Exchange it for <CRE>items</C> at a certain shop." },
            { "ITM_BDG_Info_0325", "A pin inlaid with three yellow jewels. Exchange it for <CRE>items</C> at a certain shop." },
            { "ITM_BDG_Info_0326", "A pin inlaid with four green jewels. Exchange it for <CRE>items</C> at a certain shop." },
            { "ITM_BDG_Info_0327", "A pin inlaid with five light blue jewels. Exchange it for <CRE>items</C> at a certain shop." },
            { "ITM_BDG_Info_0328", "A pin inlaid with six blue jewels. Exchange it for <CRE>items</C> at a certain shop." },
            { "ITM_BDG_Info_0329", "A pin inlaid with seven purple jewels. Exchange it for <CRE>items</C> at a certain shop." },
            { "ITM_BDG_Info_0330", "A pin reminiscent of the world of memories. Exchange it for <CRE>items</C> at a certain shop." },
            { "ITM_BDG_Info_0331", "A pin reminiscent of a transient world. Exchange it for <CRE>items</C> at a certain shop." },
            { "ITM_BDG_Info_0332", "A pin reminiscent of an unseen world. Exchange it for <CRE>items</C> at a certain shop." },
