            { "ITM_BDG_Info_0038", "Frappe la cible avec votre lance. Continuez à attaquer pour <CRE>repousser l'ennemi</C>. Peut parfois <CRE>désorienter</C> l'ennemi." },
            { "ITM_BDG_Info_0039", "Frappe la cible avec votre lance. Continuez à attaquer pour <CRE>repousser l'ennemi</C>. Peut parfois <CRE>désorienter</C> l'ennemi." },
            { "ITM_BDG_Info_0040", "Frappe la cible avec votre lance. Continuez à attaquer pour <CRE>repousser l'ennemi</C>. Peut parfois <CRE>fortement empoisonner</C> l'ennemi." },
            { "ITM_BDG_Info_0041", "Frappe la cible avec votre lance. Continuez à attaquer pour <CRE>repousser l'ennemi</C>. Peut parfois <CRE>fortement empoisonner</C> l'ennemi." },
            { "ITM_BDG_Info_0042", "Frappe la cible hors de son champ de vision avec un dard empoisonné. Continuez à attaquer pour <CRE>repousser l'ennemi</C>. Peut parfois <CRE>mortellement empoisonner</C> l'ennemi." },
            { "ITM_BDG_Info_0043", "Frappe la cible avec votre lance. Continuez à attaquer pour repousser l'ennemi, puis lui infliger un coup bas et le <CRE>mettre à terre</C>." },
            { "ITM_BDG_Info_0044", "Frappe la cible avec votre lance. Repousse l'ennemi avec <CRE>2</C> attaques, puis lui inflige <CRE>5</C> coups bas qui le <CRE>mettent à terre</C>." },
            { "ITM_BDG_Info_0045", "Frappe la cible avec votre lance. Repousse l'ennemi avec <CRE>3</C> attaques, puis lui inflige <CRE>7</C> coups bas qui le <CRE>mettent à terre</C>." },
            { "ITM_BDG_Info_0046", "Frappe la cible avec votre lance. Continuez à attaquer pour repousser l'ennemi, puis lui infliger un coup bas et le <CRE>mettre à terre</C>." },
            { "ITM_BDG_Info_0047", "Frappe la cible avec votre lance. Repousse l'ennemi avec <CRE>3</C> attaques, puis lui inflige <CRE>7</C> coups bas qui le <CRE>mettent à terre</C>." },
