            { "ITM_BDG_Info_0277", "<CRE>Restaure une petite partie</C> des PV de l'équipe <CRE>instantanément</C>. (<CRE>Utilisation unique</C>. La jauge du badge ne se remplit pas automatiquement et ce dernier ne peut pas être réactivé.)" },
            { "ITM_BDG_Info_0278", "<CRE>Restaure une partie</C> des PV de l'équipe <CRE>instantanément</C>. (<CRE>Utilisation unique</C>. La jauge du badge ne se remplit pas automatiquement et ce dernier ne peut pas être réactivé.)" },
            { "ITM_BDG_Info_0279", "<CRE>Restaure une grande partie</C> des PV de l'équipe <CRE>instantanément</C>. (<CRE>Utilisation unique</C>. La jauge du badge ne se remplit pas automatiquement et ce dernier ne peut pas être réactivé.)" },
