            { "ITM_BDG_Info_0039", "Affonda la lancia contro il bersaglio. Continua l'attacco per <CRE>respingere il nemico</C>. Possibilità di <CRE>confondere</C> il nemico." },
            { "ITM_BDG_Info_0040", "Affonda la lancia contro il bersaglio. Continua l'attacco per <CRE>respingere il nemico</C>. Possibilità di <CRE>avvelenamento notevole</C> del nemico." },
            { "ITM_BDG_Info_0041", "Affonda la lancia contro il bersaglio. Continua l'attacco per <CRE>respingere il nemico</C>. Possibilità di <CRE>avvelenamento notevole</C> del nemico." },
            { "ITM_BDG_Info_0042", "Colpisci il bersaglio nel suo punto cieco con la tua coda velenosa. Continua l'attacco per <CRE>respingere il nemico</C>. Possibilità di <CRE>avvelenamento fortissimo</C> del nemico." },
            { "ITM_BDG_Info_0043", "Affonda la lancia contro il bersaglio. Continua l'attacco per respingere il nemico, poi colpisci in basso per <CRE>atterrarlo</C>." },
            { "ITM_BDG_Info_0044", "Affonda la lancia contro il bersaglio. Respingi il nemico con un attacco a <CRE>2 colpi</C>, poi dai <CRE>5 colpi</C> in basso per <CRE>atterrarlo</C>." },
            { "ITM_BDG_Info_0045", "Affonda la lancia contro il bersaglio. Respingi il nemico con un attacco a <CRE>3 colpi</C>, poi dai <CRE>7 colpi</C> in basso per <CRE>atterrarlo</C>." },
