            { "SKL_Info_0014", "<PF_SKL_Info_0014>" },
            { "SKL_Info_0015", "Erhöht manchmal den Groove, der durch „Dreh auf!“-Kombos von Nagi und Minamimoto erhalten werden kann." },
            { "SKL_Info_0016", "Erhöht manchmal den Groove, den „Dreh auf!“-Kombos von Shoka und Nagi einbringen." },
