        public string GetLocale(byte LanguageId, string Key)
        {
            switch (LanguageId)
            {
                case 0:
                default:
                    if (ENG.ContainsKey(Key))
                    {
                        return ENG[Key];
                    }
                    break;

                case 1:
                    if (JPN.ContainsKey(Key))
                    {
                        return JPN[Key];
                    }
                    break;

                case 2:
                    if (SPA.ContainsKey(Key))
                    {
                        return SPA[Key];
                    }
                    break;

                case 3:
                    if (FRE.ContainsKey(Key))
                    {
                        return FRE[Key];
                    }
                    break;

                case 4:
                    if (ITA.ContainsKey(Key))
                    {
                        return ITA[Key];
                    }
                    break;

                case 5:
                    if (GER.ContainsKey(Key))
                    {
                        return GER[Key];
                    }
                    break;
            }

            return Key;
        }

        public string ReverseLookup(byte LanguageId, string Value)
        {
            switch (LanguageId)
            {
                case 0:
                default:
                    return ENG.FirstOrDefault(s => s.Value == Value).Key;
                    

                case 1:
                    return JPN.FirstOrDefault(s => s.Value == Value).Key;
                    

                case 2:
                    return SPA.FirstOrDefault(s => s.Value == Value).Key;
                    

                case 3:
                    return FRE.FirstOrDefault(s => s.Value == Value).Key;
                    

                case 4:
                    return ITA.FirstOrDefault(s => s.Value == Value).Key;
                    

                case 5:
                    return GER.FirstOrDefault(s => s.Value == Value).Key;
                    
            }
        }
